import React from 'react';
import { NextPage } from 'next';
import Error from 'next/error';
import { getDisplayName } from 'next/dist/next-server/lib/utils';
import { isLocale } from '../config/i18n/types';
import { LocaleProvider } from '../context/LocaleContext';
import { get } from 'lodash';

const withLocale = WrappedPage => {
  const WithLocale = ({ locale, ...pageProps }) => {
    console.log(locale);
    if (!locale) {
      return <Error statusCode={404} />;
    }
    return (
      <LocaleProvider lang={locale}>
        <WrappedPage {...pageProps} />
      </LocaleProvider>
    );
  };

  WithLocale.getInitialProps = ctx => {
    let pageProps = {};
    if (WrappedPage.getInitialProps) {
      pageProps = WrappedPage.getInitialProps(ctx);
    }
    const lang = get(ctx, 'query.lang', {});
    if (lang) {
      console.log(123, lang);
      if (typeof lang !== 'string' || !isLocale(lang)) {
        return { ...pageProps, locale: undefined };
      }
      console.log(456);
      return { ...pageProps, locale: lang }; //
    }
  };

  WithLocale.displayName = `withLang(${getDisplayName(WrappedPage)})`;

  return WithLocale;
};

export default withLocale;
